import React, { useState } from 'react';
import './App.css';

{/* 
  - criar um state para armazenar a jogada do computador e um randomizador. 
  - criar um state para armazenara a jogada do player.
  - exibir os resultados na tela
*/}

function App() {
  const [computer, setComputer] = useState();
  const [player, setPlayer] = useState();

  const jokenpo = () => {
    res = (Math.floor(Math.random() * 3));
    return (
      res === 0 && <div>Pedra</div>
    )
  }

  // const jogador = (props) => {
  //   setPlayer(props)
  // }
  const pedra = () => {
    setPlayer(0)
  }

  return (
    <div className="App">
      <header className="App-header">



        <div>
          <h2>Computador</h2>
          <div>Pedra Papel Tesoura</div>
        </div>
        <div>{computer}</div>
        <div>{player}</div>
        <div>
          <h2>Jogador</h2>
          <button onClick={jokenpo, pedra}>Pedra</button>
          <button onClick={jokenpo}>Papel</button>
          <button onClick={jokenpo}>Tesoura</button>
        </div>
      </header>
    </div>
  );
}

export default App;
