import React, { useState } from 'react';
import './App.css';

function App() {
  const [computer, setComputer] = useState();
  const [player, setPlayer] = useState();
  const [pointsComputer, setPointsComputer] = useState();
  const [pointsPlayer, setPointsPlayer] = useState();

  const computerRandom = () => {
    setComputer(Math.floor(Math.random() * 3));
  }

  const pedra = () => {
    setPlayer(0)
    computerRandom()
  }

  const papel = () => {
    setPlayer(1)
    computerRandom()
  }

  const tesoura = () => {
    setPlayer(2)
    computerRandom()
  }

  const winningCondition = () => {
    let res;
    if (computer === 0 && player === 0) {
      res = "Empate!"
    } else if (computer === 0 && player === 1) {
      res = "Perdeu. Tente de novo!"
    } else if (computer === 0 && player === 2) {
      res = "Você ganhou!"
    } else if (computer === 1 && player === 0) {
      res = "Você ganhou!"
    } else if (computer === 1 && player === 1) {
      res = "Empate!"
    } else if (computer === 1 && player === 2) {
      res = "Perdeu. Tente de novo!"
    } else if (computer === 2 && player === 0) {
      res = "Perdeu. Tente de novo!"
    } else if (computer === 2 && player === 1) {
      res = "Você ganhou!"
    } else if (computer === 2 && player === 2) {
      res = "Empate!"
    }
    return res;
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          {res}
        </div>
        <div>
          <h2>Computador</h2>
          <div>
            {computer}
          </div>
          <div>
            {player}
          </div>
          <h2>Jogador</h2>
          <button onClick={pedra}>Pedra</button>
          <button onClick={papel}>Papel</button>
          <button onClick={tesoura}>Tesoura</button>
        </div>
      </header>
    </div>
  );
}

export default App;
