import React, { useState } from 'react';
import './App.css';
import Results from './components/results';

function App() {
  const [computer, setComputer] = useState();
  const [player, setPlayer] = useState();

  const computerRandom = () => {
    setComputer(Math.floor(Math.random() * 3));
  }

  const pedra = () => {
    setPlayer(0)
    computerRandom()
  }

  const papel = () => {
    setPlayer(1)
    computerRandom()
  }

  const tesoura = () => {
    setPlayer(2)
    computerRandom()
  }

  const winningCondition = () => {
    (
        props.computer === props.player && <div>Empate</div>
        ||
        (   (props.computer === 0 && props.player === 1)
            ||
            (props.computer === 1 && props.player === 2)
            ||
            (props.computer === 2 && props.player === 0)    )
            && 
            <div>Você Ganhou!</div>
        ||
        (   (props.computer === 0 && props.player === 2)
            ||
            (props.computer === 1 && props.player === 0)
            ||
            (props.computer === 2 && props.player === 1)    )
            && 
            <div>Você Perdeu</div>
    )
}

  return (
    <div className="App">
      <header className="App-header">

        <div>
          <h2>Computador</h2>
          <div>Pedra Papel Tesoura</div>
        </div>
          <Results computer={computer} player={player}/>
        <div>
          <h2>Jogador</h2>
          <button onClick={pedra}>Pedra</button>
          <button onClick={papel}>Papel</button>
          <button onClick={tesoura}>Tesoura</button>
        </div>
      </header>
    </div>
  );
}

export default App;
