import React, { useState } from 'react';
import './App.css';

function App() {
  const [computer, setComputer] = useState();
  const [player, setPlayer] = useState();

  const computerRandom = () => {
    const res = (Math.floor(Math.random() * 3));
    return (
      setComputer(
        res === 0 && <div>Pedra</div> 
        || 
        res === 1 && <div>Papel</div>
        ||
        res === 2 && <div>Tesoura</div>
      )
    )
  }

  // const jogador = (props) => {
  //   setPlayer(props)
  // }
  const pedra = () => {
    setPlayer(0)
    computerRandom()
  }

  return (
    <div className="App">
      <header className="App-header">



        <div>
          <h2>Computador</h2>
          <div>Pedra Papel Tesoura</div>
        </div>
        <div>{computer}</div>
        <div>{player}</div>
        <div>
          <h2>Jogador</h2>
          <button onClick={pedra}>Pedra</button>
          <button onClick={jokenpo}>Papel</button>
          <button onClick={jokenpo}>Tesoura</button>
        </div>
      </header>
    </div>
  );
}

export default App;
