import React, { useState } from 'react';
import './App.css';

function App() {
  const [computer, setComputer] = useState();
  const [player, setPlayer] = useState();

  const computerRandom = () => {
    setComputer(Math.floor(Math.random() * 3));
  }

  const pedra = () => {
    setPlayer(0)
    computerRandom()
  }

  const papel = () => {
    setPlayer(1)
    computerRandom()
  }

  const tesoura = () => {
    setPlayer(2)
    computerRandom()
  }

  return (
    <div className="App">
      <header className="App-header">

        {console.log(player)}
        <div>
          <h2>Computador</h2>
          <div>Pedra Papel Tesoura</div>
        </div>
        <div>{computer}</div>
        <div>{player}</div>
        <div>
          <h2>Jogador</h2>
          <button onClick={pedra}>Pedra</button>
          <button onClick={papel}>Papel</button>
          <button onClick={tesoura}>Tesoura</button>
        </div>
      </header>
    </div>
  );
}

export default App;
