import React, { useState } from 'react';
import './App.css';

{/* 
  - criar um state para armazenar a jogada do computador e um randomizador. 
  - criar um state para armazenara a jogada do player.
  - exibir os resultados na tela
*/}

function App() {
  const [computador, setComputador] = useState();

  const jokenpo = () => {
    setComputador(Math.floor(Math.random() * 3))
  }

  return (
    <div className="App">
      <header className="App-header">



        <div>
          <h2>Computador</h2>
          <div>Pedra Papel Tesoura</div>
        </div>
        <div>{computador}</div>
        <div>
          <h2>Jogador</h2>
          <button onClick={jokenpo}>Pedra</button>
          <button>Papel</button>
          <button>Tesoura</button>
        </div>
      </header>
    </div>
  );
}

export default App;
