import React, { useState, useEffect } from 'react';
import './App.css';

// Falta: 
//   - Como colocar botão de reset
//   - deixar os ifs mais bonitos

function App() {
  const [computer, setComputer] = useState();
  const [player, setPlayer] = useState();
  const [pointsComputer, setPointsComputer] = useState(0);
  const [pointsPlayer, setPointsPlayer] = useState(0);
  const [results, setResults] = useState("Escolha uma jogada");

  const computerRandom = () => {
    let res = Math.floor(Math.random() * 3);
    if (res === 0) {
      setComputer("Pedra")
    } else if (res === 1) {
      setComputer("Papel")
    } else {
      setComputer("Tesoura")
    }
  }

  const pedra = () => {
    setPlayer("Pedra")
    computerRandom()
  }

  const papel = () => {
    setPlayer("Papel")
    computerRandom()
  }

  const tesoura = () => {
    setPlayer("Tesoura")
    computerRandom()
  }

  const reset = () => {
    setPlayer("")
    setComputer("")
  }

  useEffect(() => {
    winningCondition();
  }, [player]);

  const winningCondition = () => {
    let res;

    if (computer === player) {
      res = "Empate."
    } else if (player === "Pedra" && computer === "Tesoura") {
      res = "Você ganhou!"
      setPointsPlayer(pointsPlayer + 1)
    } else if (player === "Papel" && computer === "Pedra") {
      res = "Você ganhou!"
      setPointsPlayer(pointsPlayer + 1)
    } else if (player === "Tesoura" && computer === "Papel") {
      res = "Você ganhou!"
      setPointsPlayer(pointsPlayer + 1)
    } else {
      res = "Perdeu. Tente de novo!"
      setPointsComputer(pointsComputer + 1)
    }





    if (computer === "Pedra" && player === "Pedra") {
      res = "Empate!"
      setResults(res);
    } else if (computer === "Pedra" && player === "Papel") {
      res = "Você ganhou!"
      setResults(res);
      setPointsPlayer(pointsPlayer + 1)
    } else if (computer === "Pedra" && player === "Tesoura") {
      res = "Perdeu. Tente de novo!"
      setResults(res);
      setPointsComputer(pointsComputer + 1)
    } else if (computer === "Papel" && player === "Pedra") {
      res = "Perdeu. Tente de novo!"
      setResults(res);
      setPointsComputer(pointsComputer + 1)
    } else if (computer === "Papel" && player === "Papel") {
      res = "Empate!"
      setResults(res);
    } else if (computer === "Papel" && player === "Tesoura") {
      res = "Você ganhou!"
      setResults(res);
      setPointsPlayer(pointsPlayer + 1)
    } else if (computer === "Tesoura" && player === "Pedra") {
      res = "Você ganhou!"
      setResults(res);
      setPointsPlayer(pointsPlayer + 1)
    } else if (computer === "Tesoura" && player === "Papel") {
      res = "Perdeu. Tente de novo!"
      setResults(res);
      setPointsComputer(pointsComputer + 1)
    } else if (computer === "Tesoura" && player === "Tesoura") {
      res = "Empate!"
      setResults(res);
    };
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          Computador {pointsComputer} | {pointsPlayer} Jogador
        </div>
        <div>
          {results}
        </div>
        <div>
          <h2>Computador</h2>
          <div>
            {computer}
          </div>
          <div>
            {player}
          </div>
          <h2>Jogador</h2>
          <div>
            <button onClick={pedra}>Pedra</button>
            <button onClick={papel}>Papel</button>
            <button onClick={tesoura}>Tesoura</button>
          </div>
        </div>
        <div>
          <button onClick={reset}>Jogar Novamente</button>
        </div>
      </header>
    </div>
  );
}

export default App;
